var  i=0;
function Nav() {
    if(i===0)
    {
        openNav();
        i=1;
    }
    else
    {
        closeNav();
        i=0
    }
}
function openNav() {
    document.getElementById("mySidenav").style.width = "320px";
    document.getElementById("menu").style.marginLeft = "320px";
}

function closeNav() {
    document.getElementById("mySidenav").style.width = "0";
    document.getElementById("menu").style.marginLeft= "0";
    document.getElementById("main").style.position = "absolute";
}

var j=0;
function Sidenav() {
    if(j===0)
    {
        openSidenav();
        j=1;
    }
    else
    {
        closeSidenav();
        j=0
    }
}
function openSidenav() {
    document.getElementById("left_nav_box_list").style.display = "block";
}

function closeSidenav() {
    document.getElementById("left_nav_box_list").style.display = "none";

}

function chg(){
    var el = document.getElementById("pls");
    if (el.src.indexOf("img/plus.png")>0){
        el.src="img/minus.png";
    }
    else{
        el.src="img/plus.png";
    }
}


